const pergunta = $input.first().json.pergunta;

const promptClassificador = `Voc√™ √© um Classificador de Inten√ß√£o. Analise a pergunta e retorne APENAS um JSON v√°lido (sem markdown) com:
{
  "ano": 2025,
  "tipo_analise": "financeira",
  "eh_retirada": false,
  "valor_retirada": 0,
  "pergunta_original": "texto"
}

REGRAS:
- ano: Se mencionar 'ano passado' ou '2025' = 2025. Se 'agora', 'futuro' ou '2026' = 2026
- tipo_analise: 'financeira', 'vendas' ou 'operacional'
- eh_retirada: true se pergunta for sobre retirar/sacar/pegar dinheiro
- valor_retirada: extraia o valor num√©rico se houver (ex: "10 mil" = 10000, "50k" = 50000)

PERGUNTA: ${pergunta}`;

return {
  json: {
    contents: [{
      role: "user",
      parts: [{
        text: promptClassificador
      }]
    }],
    generationConfig: {
      temperature: 0.1,
      maxOutputTokens: 300
    }
  }
};

const resposta = $input.first().json.content.parts[0].text
let jsonLimpo = resposta.replace(/```json\n?/g, '').replace(/```/g, '').trim();
const classificacao = JSON.parse(jsonLimpo);
const targetFile = `Controle - ${classificacao.ano}`;

return {
  json: {
    ano: classificacao.ano,
    tipo_analise: classificacao.tipo_analise,
    eh_retirada: classificacao.eh_retirada || false,
    valor_retirada: classificacao.valor_retirada || 0,
    pergunta_original: classificacao.pergunta_original,
    target_worksheet: targetFile
  }
};

// Limpa e estrutura dados do Dashboard
const items = $input.all();
const resultado = {
  caixa: null,
  janeiro: {
    receber: null,
    recebido: null,
    pagar: null,
    pago: null
  },
  saldo: {},
  caixaProjetado: {}
};

// Busca valores espec√≠ficos nas linhas
for (let i = 0; i < items.length; i++) {
  const row = items[i].json.row;
  if (!row) continue;

  // Linha 5 col 1: 'Caixa' -> valor na linha 7 col 1
  if (row[1] === 'Caixa' && i + 2 < items.length) {
    const valorRow = items[i + 2].json.row;
    if (valorRow && typeof valorRow[1] === 'number') {
      resultado.caixa = valorRow[1];
    }
  }

  // Linha 5 col 9: 'Receber (Janeiro)' -> valor na linha 7 col 9
  if (row[9] === 'Receber (Janeiro)' && i + 2 < items.length) {
    const valorRow = items[i + 2].json.row;
    if (valorRow && typeof valorRow[9] === 'number') {
      resultado.janeiro.receber = valorRow[9];
    }
  }

  // Linha 12 col 9: 'Recebido (Janeiro)' -> valor na linha 14 col 9
  if (row[9] === 'Recebido (Janeiro)' && i + 2 < items.length) {
    const valorRow = items[i + 2].json.row;
    if (valorRow && typeof valorRow[9] === 'number') {
      resultado.janeiro.recebido = valorRow[9];
    }
  }

  // Linha 19 col 9: 'Pagar (Janeiro)' -> valor na linha 21 col 9
  if (row[9] === 'Pagar (Janeiro)' && i + 2 < items.length) {
    const valorRow = items[i + 2].json.row;
    if (valorRow && typeof valorRow[9] === 'number') {
      resultado.janeiro.pagar = valorRow[9];
    }
  }

  // Linha 26 col 9: 'Pago (Janeiro)' -> valor na linha 28 col 9
  if (row[9] === 'Pago (Janeiro)' && i + 2 < items.length) {
    const valorRow = items[i + 2].json.row;
    if (valorRow && typeof valorRow[9] === 'number') {
      resultado.janeiro.pago = valorRow[9];
    }
  }

  // Linha 33 col 9: 'Caixa Projetado (Janeiro)' -> valor na linha 35 col 9
  if (row[9] === 'Caixa Projetado (Janeiro)' && i + 2 < items.length) {
    const valorRow = items[i + 2].json.row;
    if (valorRow && typeof valorRow[9] === 'number') {
      resultado.caixaProjetado.Janeiro = valorRow[9];
    }
  }

  // Linha 57: meses (Janeiro na col 4)
  if (row[4] === 'Janeiro' && i + 1 < items.length && i + 2 < items.length) {
    const entradaRow = items[i + 1].json.row;
    const saidaRow = items[i + 2].json.row;

    if (entradaRow && saidaRow) {
      const entrada = entradaRow[4] || 0;
      const saida = saidaRow[4] || 0;
      resultado.saldo.Janeiro = entrada - saida;
    }
  }
}

return { json: resultado };

// Limpa e estrutura dados do Fluxo de Caixa
const items = $input.all();
const mesesNomes = ['Janeiro', 'Fevereiro', 'Mar√ßo', 'Abril', 'Maio', 'Junho', 
                    'Julho', 'Agosto', 'Setembro', 'Outubro', 'Novembro', 'Dezembro'];

const mesAtual = new Date().getMonth();
const nomeDoMesAtual = mesesNomes[mesAtual];

const resultado = {
  realizado: {
    saldoInicial: {},
    entradas: {},
    saidas: {},
    caixaFinal: {}
  },
  emAndamento: {
    entradasAReceber: {},
    saidasAPagar: {},
    movimentacaoTotal: {},
    saldo: {}
  }
};

// Processa cada linha do fluxo
items.forEach((item, idx) => {
  const row = item.json.row;
  if (!row || row.length === 0) return;

  const primeiraColuna = row[0];
  if (!primeiraColuna) return;

  // Linha 3: Saldo Inicial (apenas m√™s atual)
  if (primeiraColuna === 'Saldo Inicial') {
    mesesNomes.forEach((mes, i) => {
      const valor = row[i + 1];
      if (valor && typeof valor === 'number' && mes === nomeDoMesAtual) {
        resultado.realizado.saldoInicial[mes] = valor;
      }
    });
  }

  // Linha 4: Entradas
  if (primeiraColuna === 'Entradas') {
    mesesNomes.forEach((mes, i) => {
      const valor = row[i + 1];
      if (valor && typeof valor === 'number' && valor !== 0) {
        resultado.realizado.entradas[mes] = { total: valor };
      }
    });
  }

  // Linha 8: Sa√≠das
  if (primeiraColuna === 'Sa√≠das') {
    mesesNomes.forEach((mes, i) => {
      const valor = row[i + 1];
      if (valor && typeof valor === 'number' && valor !== 0) {
        resultado.realizado.saidas[mes] = { total: valor };
      }
    });
  }

  // Linha 19: Caixa (Caixa Final, apenas m√™s atual)
  if (primeiraColuna === 'Caixa') {
    mesesNomes.forEach((mes, i) => {
      const valor = row[i + 1];
      if (valor && typeof valor === 'number' && mes === nomeDoMesAtual) {
        resultado.realizado.caixaFinal[mes] = valor;
      }
    });
  }

  // Linha 22: Entradas (A receber)
  if (primeiraColuna === 'Entradas (A receber)') {
    for (let i = 0; i < 5; i++) {
      const valor = row[i + 1];
      if (valor && typeof valor === 'number' && valor !== 0) {
        resultado.emAndamento.entradasAReceber[mesesNomes[i]] = { total: valor };
      }
    }
  }

  // Linha 26: Sa√≠das (A pagar)
  if (primeiraColuna === 'Sa√≠das (A pagar)') {
    for (let i = 0; i < 5; i++) {
      const valor = row[i + 1];
      if (valor && typeof valor === 'number' && valor !== 0) {
        resultado.emAndamento.saidasAPagar[mesesNomes[i]] = { total: valor };
      }
    }
  }

  // Linha 36: Movimenta√ß√£o Total
  if (primeiraColuna === 'Movimenta√ß√£o Total') {
    for (let i = 0; i < 5; i++) {
      const valor = row[i + 1];
      if (valor && typeof valor === 'number') {
        resultado.emAndamento.movimentacaoTotal[mesesNomes[i]] = valor;
      }
    }
  }

  // Linha 37: Saldo
  if (primeiraColuna === 'Saldo') {
    for (let i = 0; i < 5; i++) {
      const valor = row[i + 1];
      if (valor && typeof valor === 'number' && valor !== 0) {
        resultado.emAndamento.saldo[mesesNomes[i]] = valor;
      }
    }
  }
});

return { json: resultado };

// Combina dados do Dashboard e Fluxo de Caixa
const items = $input.all();
const dadosCombinados = {
  dashboard: {},
  fluxoCaixa: {},
  resumoFinanceiro: {}
};

items.forEach(item => {
  const data = item.json;

  if (data.caixa !== undefined) {
    dadosCombinados.dashboard = data;
    dadosCombinados.resumoFinanceiro.caixaAtual = data.caixa;
    if (data.janeiro) {
      dadosCombinados.resumoFinanceiro.receberJaneiro = data.janeiro.receber;
      dadosCombinados.resumoFinanceiro.pagarJaneiro = data.janeiro.pagar;
      dadosCombinados.resumoFinanceiro.saldoJaneiro = data.saldo?.Janeiro;
    }
  } else if (data.realizado !== undefined) {
    dadosCombinados.fluxoCaixa = data;
    if (data.realizado.saldoInicial?.Janeiro) {
      dadosCombinados.resumoFinanceiro.saldoInicialMes = data.realizado.saldoInicial.Janeiro;
    }
    if (data.emAndamento) {
      dadosCombinados.resumoFinanceiro.projecaoProximosMeses = data.emAndamento.movimentacaoTotal;
    }
  }
});

return { json: dadosCombinados };

const classificacao = $node['Parse Classifica√ß√£o'].json;
const perguntaOriginal = classificacao.pergunta_original;
const ehRetirada = classificacao.eh_retirada;
const valorRetirada = classificacao.valor_retirada;
const dados = $input.first().json;

let contextoFinanceiro = "üìä DADOS FINANCEIROS CONSOLIDADOS\n\n";

contextoFinanceiro += "=== DASHBOARD (Vis√£o Atual) ===\n";
if (dados.dashboard.caixa) {
  contextoFinanceiro += `üí∞ Caixa Atual: R$ ${dados.dashboard.caixa.toLocaleString('pt-BR', {minimumFractionDigits: 2})}\n`;
}
if (dados.dashboard.janeiro) {
  contextoFinanceiro += `\nüì• JANEIRO - Recebimentos:\n`;
  contextoFinanceiro += `   A Receber: R$ ${(dados.dashboard.janeiro.receber || 0).toLocaleString('pt-BR', {minimumFractionDigits: 2})}\n`;
  contextoFinanceiro += `   J√° Recebido: R$ ${(dados.dashboard.janeiro.recebido || 0).toLocaleString('pt-BR', {minimumFractionDigits: 2})}\n`;
  contextoFinanceiro += `\nüì§ JANEIRO - Pagamentos:\n`;
  contextoFinanceiro += `   A Pagar: R$ ${(dados.dashboard.janeiro.pagar || 0).toLocaleString('pt-BR', {minimumFractionDigits: 2})}\n`;
  contextoFinanceiro += `   J√° Pago: R$ ${(dados.dashboard.janeiro.pago || 0).toLocaleString('pt-BR', {minimumFractionDigits: 2})}\n`;
}

contextoFinanceiro += "\n=== FLUXO DE CAIXA (Hist√≥rico + Proje√ß√£o) ===\n";
if (dados.fluxoCaixa.realizado) {
  const real = dados.fluxoCaixa.realizado;
  if (real.saldoInicial?.Janeiro) {
    contextoFinanceiro += `üèÅ Saldo Inicial Janeiro: R$ ${real.saldoInicial.Janeiro.toLocaleString('pt-BR', {minimumFractionDigits: 2})}\n`;
  }
  if (real.entradas?.Janeiro) {
    contextoFinanceiro += `‚úÖ Entradas Realizadas Jan: R$ ${real.entradas.Janeiro.total.toLocaleString('pt-BR', {minimumFractionDigits: 2})}\n`;
  }
  if (real.saidas?.Janeiro) {
    contextoFinanceiro += `‚ùå Sa√≠das Realizadas Jan: R$ ${real.saidas.Janeiro.total.toLocaleString('pt-BR', {minimumFractionDigits: 2})}\n`;
  }
}

if (dados.fluxoCaixa.emAndamento) {
  const andamento = dados.fluxoCaixa.emAndamento;
  contextoFinanceiro += "\nüìà PROJE√á√ïES (Pr√≥ximos Meses):\n";

  ['Janeiro', 'Fevereiro', 'Mar√ßo'].forEach(mes => {
    if (andamento.movimentacaoTotal?.[mes]) {
      const mov = andamento.movimentacaoTotal[mes];
      const simbolo = mov >= 0 ? '‚ûï' : '‚ûñ';
      contextoFinanceiro += `   ${simbolo} ${mes}: R$ ${mov.toLocaleString('pt-BR', {minimumFractionDigits: 2})}\n`;
    }
  });
}

let promptCEO = `Voc√™ √© o CEO da Deva Cardans. Voc√™ tem acesso aos dados financeiros consolidados.

üî¥ REGRA DE OURO: O saldo em caixa NUNCA pode ficar abaixo de R$ 60.000,00 (margem de seguran√ßa obrigat√≥ria).

TAREFA: Analise os dados e responda a pergunta de forma estrat√©gica e objetiva.

Se a pergunta envolver compras/retiradas:
1. Verifique o saldo atual
2. Simule o impacto (Caixa Atual - Valor Solicitado - Compromissos Pendentes)
3. Compare com a margem de seguran√ßa de R$ 60.000,00
4. Se o saldo final ficar abaixo de R$ 60k, ALERTE o risco
5. Considere as proje√ß√µes dos pr√≥ximos meses
6. Sempre mencione explicitamente a margem de seguran√ßa de R$ 60k na resposta`;

if (ehRetirada) {
  promptCEO += `

üö® ATEN√á√ÉO ESPECIAL: Esta √© uma pergunta sobre RETIRADA DE DINHEIRO.`;
  if (valorRetirada > 0) {
    promptCEO += `
Valor solicitado: R$ ${valorRetirada.toLocaleString('pt-BR', {minimumFractionDigits: 2})}`;
  }
  promptCEO += `

‚ö†Ô∏è REGRA CR√çTICA PARA RETIRADAS:
Se voc√™ NEGAR ou DESACONSELHAR a retirada por risco financeiro:
1. CALCULE e INFORME o valor m√°ximo seguro que pode ser retirado
2. Use a f√≥rmula: Valor M√°ximo = (Caixa Atual - Contas a Pagar - Margem de Seguran√ßa 60k)
3. Apresente de forma clara: "‚úÖ Valor seguro para retirada: R$ X,XXX.XX"
4. Explique o c√°lculo brevemente

Sempre que negar uma retirada, voc√™ DEVE sugerir um valor alternativo vi√°vel.`;
}

promptCEO += `

RESPONDA EM PORTUGU√äS, de forma clara e direta.

${contextoFinanceiro}

PERGUNTA: ${perguntaOriginal}`;

return {
  json: {
    contents: [
      {
        role: "user",
        parts: [{ text: promptCEO }]
      }
    ],
    generationConfig: {
      temperature: 0.3,
      maxOutputTokens: 2000
    }
  }
};

// Extrai resposta do Gemini (formato pode variar)
const input = $input.first().json;
let respostaCEO = '';

// Tenta diferentes formatos de resposta
if (input.candidates && input.candidates[0]) {
  respostaCEO = input.candidates[0].content.parts[0].text;
} else if (input.content && input.content.parts) {
  respostaCEO = input.content.parts[0].text;
} else if (input.text) {
  respostaCEO = input.text;
} else if (typeof input === 'string') {
  respostaCEO = input;
} else {
  respostaCEO = JSON.stringify(input, null, 2);
}

// Busca dados da classifica√ß√£o
const classificacao = $node['Parse Classifica√ß√£o'].json;
const ehRetirada = classificacao.eh_retirada;
const valorRetirada = classificacao.valor_retirada || 0;

// Extrai informa√ß√µes da resposta detalhada
let decisao = "INFORMA√á√ÉO";
let valorMaximoSeguro = null;
let resumo = "";

if (ehRetirada) {
  // Detecta se foi aprovada ou negada
  const respostaLower = respostaCEO.toLowerCase();

  if (respostaLower.includes('negar') || respostaLower.includes('negada') || 
      respostaLower.includes('n√£o autorizo') || respostaLower.includes('n√£o posso aprovar') ||
      respostaLower.includes('desaconselho') || respostaLower.includes('n√£o √© vi√°vel')) {
    decisao = "NEGADA";
  } else if (respostaLower.includes('aprovar') || respostaLower.includes('aprovada') || 
             respostaLower.includes('autorizo') || respostaLower.includes('pode ser realizada')) {
    decisao = "APROVADA";
  } else if (respostaLower.includes('aten√ß√£o') || respostaLower.includes('cuidado') ||
             respostaLower.includes('risco')) {
    decisao = "APROVADA COM RESSALVAS";
  }

  // Extrai valor m√°ximo seguro
  const regexValorMaximo = /valor\s+(?:m√°ximo\s+)?seguro.*?r\$\s*([\d.,]+)/i;
  const matchValor = respostaCEO.match(regexValorMaximo);
  if (matchValor && matchValor[1]) {
    const valorStr = matchValor[1].replace(/\./g, '').replace(',', '.');
    valorMaximoSeguro = parseFloat(valorStr);
  }

  // Gera resumo
  if (decisao === "NEGADA") {
    resumo = `Retirada negada. Valor solicitado: R$ ${valorRetirada.toLocaleString('pt-BR', {minimumFractionDigits: 2})}. `;
    if (valorMaximoSeguro !== null) {
      resumo += `Valor m√°ximo seguro: R$ ${valorMaximoSeguro.toLocaleString('pt-BR', {minimumFractionDigits: 2})}. `;
    }
    if (respostaLower.includes('margem de seguran√ßa') || respostaLower.includes('60')) {
      resumo += "Motivo: Risco de violar a margem de seguran√ßa de R$ 60.000,00.";
    }
  } else if (decisao === "APROVADA") {
    resumo = `Retirada aprovada. Valor: R$ ${valorRetirada.toLocaleString('pt-BR', {minimumFractionDigits: 2})}. Saldo permanecer√° dentro da margem de seguran√ßa.`;
  } else if (decisao === "APROVADA COM RESSALVAS") {
    resumo = `Retirada poss√≠vel com ressalvas. Valor: R$ ${valorRetirada.toLocaleString('pt-BR', {minimumFractionDigits: 2})}. Aten√ß√£o √†s proje√ß√µes futuras.`;
  }
} else {
  // Para perguntas n√£o relacionadas a retirada
  resumo = "An√°lise financeira fornecida na resposta detalhada.";
}

return {
  json: {
    resposta_detalhada: respostaCEO,
    decisao: decisao,
    valor_solicitado: valorRetirada,
    valor_maximo_seguro: valorMaximoSeguro,
    resumo: resumo,
    status: "success",
    timestamp: new Date().toISOString(),
    versao: "v1.1 - Hybrid Approach"
  }
};
